import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import { useState } from "react";
import MyModal from "./Modal";
import Button from "@mui/material/Button";
import { styled } from "@mui/material/styles";

const StyledTableCell = styled(StyledTableCell)(({ theme }) => ({
    [`&.${StyledTableCellClasses.head}`]: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    [`&.${StyledTableCellClasses.body}`]: {
      fontSize: 14,
    },
  }));
  
  const StyledTableRow = styled(TableRow)(({ theme }) => ({
    "&:nth-of-type(odd)": {
      backgroundColor: theme.palette.action.hover,
    },
    // hide last border
    "&:last-child td, &:last-child th": {
      border: 0,
    },
  }));

export default function Database(props) {

    const [selectedRow, setSelectedRow] = useState();
    const [open, setOpen] = React.useState(false);
    const handleOpen = (id) => {
    const data = props.mData;
    const row = data.filter((row) => row.id == id);
    setSelectedRow(row);
    setOpen(true);
    console.log("row", row);
  };

  const closeModal = () => {
    setOpen(false);
  };

    return (<>
    {open && (
        <MyModal
          open={open}
          selectedRow={selectedRow}
          closeModal={() => closeModal()}
        />
      )}
        <TableContainer component={Paper}>
        <Table sx={{ minWidth: 600 }} aria-label="compare table" stickyHeader>
          <TableHead>
            <TableRow>
              <StyledTableCell
                align="left"
                sx={{
                  backgroundColor: "#141414",
                  color: "#fff",
                  maxHeight: "40px",
                }}
              >
                Full Name
              </StyledTableCell>
              <StyledTableCell
                align="left"
                sx={{
                  backgroundColor: "#141414",
                  color: "#fff",
                  maxHeight: "40px",
                }}
              >
                Experience
              </StyledTableCell>
              <StyledTableCell
                align="left"
                sx={{
                  backgroundColor: "#141414",
                  color: "#fff",
                  maxHeight: "40px",
                }}
              >
                Latest Employer
              </StyledTableCell>
              <StyledTableCell
                align="left"
                sx={{
                  backgroundColor: "#141414",
                  color: "#fff",
                  maxHeight: "40px",
                }}
              >
                Inferred Salary in USD
              </StyledTableCell>
              
              <StyledTableCell
                align="left"
                sx={{
                  backgroundColor: "#141414",
                  color: "#fff",
                  maxHeight: "40px",
                }}
              >
                Location
              </StyledTableCell>
              <StyledTableCell
                align="left"
                sx={{
                  backgroundColor: "#141414",
                  color: "#fff",
                  maxHeight: "40px",
                }}
              >
                
              </StyledTableCell>
            </TableRow>
          </TableHead>
          <TableBody>
            {props.mData.map((row, id) => (
              <TableRow key={id}>
                <StyledTableCell align="left">{row.full_name.toUpperCase()}</StyledTableCell>
                <StyledTableCell align="left">
                  {row?.inferred_years_experience}
                </StyledTableCell>
                <StyledTableCell align="left">
                  {row.job_company_name.toUpperCase()}
                </StyledTableCell>
                <StyledTableCell align="left">{row.inferred_salary}
                </StyledTableCell>
                <StyledTableCell align="left">{row.location_region.toUpperCase()}</StyledTableCell>
                <StyledTableCell align="center">
                      <Button
                        variant="contained"
                        color="primary"
                        id={row.id}
                        onClick={(e) => {
                          handleOpen(e.target.id);
                        }}
                      >
                        Profile
                      </Button>
                    </StyledTableCell>
                
              </TableRow>
            ))}
          </TableBody>
        </Table>
      </TableContainer>
      </>
    );
  }
