import { useEffect, useState } from "react";
import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import axios from "axios";
import TextField from "@mui/material/TextField";
import FormControl from "@mui/material/FormControl";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import ListItemText from "@mui/material/ListItemText";
import Select from "@mui/material/Select";
import Checkbox from "@mui/material/Checkbox";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import skillsSelection from "./components/SkillsSearch";
import { useTheme } from '@mui/material/styles';
import Chip from '@mui/material/Chip';
import { Slider } from "@mui/material";
import { styled, Box } from '@mui/system';
import ModalUnstyled from '@mui/core/ModalUnstyled';
import { Dialog, Transition } from '@headlessui/react'
import { Fragment } from "react";
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';

const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

// const StyledModal = styled(ModalUnstyled)`
//   position: fixed;
//   z-index: 1300;
//   right: 0;
//   bottom: 0;
//   top: 0;
//   left: 0;
//   display: flex;
//   align-items: center;
//   justify-content: center;
// `;

// const Backdrop = styled('div')`
//   z-index: -1;
//   position: fixed;
//   right: 0;
//   bottom: 0;
//   top: 0;
//   left: 0;
//   background-color: rgba(0, 0, 0, 0.5);
//   -webkit-tap-highlight-color: transparent;
// `;

// const style = {
//   width: 400,
//   bgcolor: 'background.paper',
//   border: '2px solid #000',
//   p: 2,
//   px: 4,
//   pb: 3,
// };

const skillsMenu = [
  "javascript",
  "jquery",
  "node.js",
  "express.js",
  "react.js",
  "mysql",
  "mongodb",
  "html",
  "mvc",
  "css",
  "matlab",
  "chemical engineering",
  "microsoft office",
  "data analysis",
  "engineering",
  "microsoft excel",
  "powerpoint",
  "research",
  "chemistry",
  "autocad",
  "microsoft word",
  "comsol",
  "thermodynamics",
  "ir spectroscopy",
  "process engineering",
  "solidworks",
  "distillation",
  "organic chemistry",
  "teamwork",
  "technical writing",
  "uv/vis",
  "nmr",
  "ftir",
  "hplc",
  "gas chromatography",
  "materials science",
  "pro/ii",
  "cascading style sheets",
  "c++",
  "python",
  "c#",
  "java",
  "check",
];

function App() {
  const [fetchData, setFetchData] = useState([]);
  const [mainData, setMainData] = useState([]);
  const [experience, setExperience] = useState("");
  const [skills, setSkills] = useState([]);
  const [locations, setLocations] = useState([]);
  const [skillsAtSearch, setSkillsAtSearch] = useState("");
  const [locationAtSearch, setLocationAtSearch] = useState([]);


  useEffect(()=> {
    axios
      .post("https://dev.weekday.technology/adhoc/candidateSearchAssignment")
      .then((response) => {
        var d = [];
        var locaData = [];
        var skillData = []
        response.data.list.map((x) =>{
          var item= x;
          var entity = item;
          entity["rawData"] = JSON.parse(item["rawData"]);
          item.rawData.map((element)=>{
            d.push(element);

          })
        })
        setMainData(d);
        setFetchData(d);

        d.map((ele)=>{
          locaData.push(ele.location_region)
        });
        d.map((ele)=>{
          for(var i=0; i < ele.skills.length; i++)
          skillData.push(ele.skills[i])
        });
        var skillData1 = [...new Set(skillData)]
          setSkills(skillData1);

        var locaData1 = [...new Set(locaData)]
        setLocations(locaData1);

      })
      .catch((e)=>{
        console.log("Error", e);
      });
  }, []);


  const handleFilterClick = () => { 
    let filter = [...fetchData];
    var i;
    if (experience) {
      console.log(experience);
      for (i = 0; i < filter.length; i++) {
        let item = filter[i];
        if (item["inferred_years_experience"] < parseInt(experience)) {
          filter.splice(i, 1);
          i--;
        }
      }
    }
    console.log(filter);
    if (skillsAtSearch) {
      let newData = [];
      for (i = 0; i < filter.length; i++) {
        let test = filter[i];
        for (var j = 0; j < skills.length; j++) {
          if (test["skills"].indexOf(skillsAtSearch) > -1) {
            newData.push(test);
            break;
          }
        }
      }
      filter = newData;

      console.log(filter);
      if (locationAtSearch.length > 0) {
        let newData = [];
        for (i = 0; i < filter.length; i++) {
          let test = filter[i];
          for (var j = 0; j < locationAtSearch.length; j++) {
            if (test["location_region"].indexOf(locationAtSearch[j]) > -1) {
              newData.push(test);
              break;
            }
          }
        }
        filter = newData;
      }
    }

  setMainData(filter);

  };

  const handleClearFilters = () => {
    setMainData(fetchData);
    setExperience("");
    setSkillsAtSearch("");
    setLocationAtSearch([]);
  };

  const handleLocationChange = (event) => {
    const {
      target: { value },
    } = event;
    setLocationAtSearch(typeof value === "string" ? value.split(",") : value);
  };


  console.log(fetchData)
  return (
    <div class="px-2 pt-4 w-full">
      <div class="flex flex-row w-full items-center">
      <div class="w-1/4 pr-2">
          <FormControl fullWidth>
            <InputLabel id="location">Location</InputLabel>
            <Select
              sx={{ borderRadius: 0 }}
              size="small"
              labelId="location"
              id="location"
              multiple
              value={locationAtSearch}
              onChange={handleLocationChange}
              input={<OutlinedInput label="Skills" />}
              renderValue={(selected) => selected.join(", ")}
            >
              {locations?.map((obj) => (
                <MenuItem key={obj} value={obj}>
                  <Checkbox checked={locationAtSearch.indexOf(obj) > -1} />
                  <ListItemText primary={obj} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>

        <div class="w-1/4 pr-8 pl-4" >
          <FormControl fullWidth>
          <TextField id="standard-basic" label="Min Experience" value={experience} variant="standard" onChange={(e) => {
                setExperience(e.target.value);
              }}/>
          </FormControl>
          <Slider
                aria-label="Temperature"
                defaultValue={3}
                // getAriaValueText={experience}
                valueLabelDisplay="auto"
                step={1}
                marks
                min={0}
                max={10}
                value={experience}
                onChange={(e) => {
                  setExperience(e.target.value);
                }}
              />
        </div>
        <div class="w-1/4 flex flex-row">
          <div class="pr-2">
            <Button
              variant="contained"
              sx={{ borderRadius: 0 }}
              onClick={handleFilterClick}
            >
              Filter
            </Button>
          </div>
          <div>
            <Button
              variant="contained"
              sx={{ borderRadius: 0 }}
              onClick={handleClearFilters}
            >
              Clear Filters
            </Button>
          </div>
        </div>
      </div>
      <div class="pt-4">
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 600 }} aria-label="compare table" stickyHeader>
            <TableHead>
              <TableRow>
                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Full Name
                </TableCell>
                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Experience
                </TableCell>

                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Location
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {mainData?.map((row, id) => (
                <TableRow key={id}>
                  <TableCell align="left">{row?.full_name.toUpperCase()}</TableCell>
                  <TableCell align="left">
                    {row?.inferred_years_experience}
                  </TableCell>

                  <TableCell align="left">{row?.location_locality.toUpperCase()}</TableCell>
                   <TableCell align="center"><Button variant="contained" color="success" onClick={handleOpen}>Profile</Button>
                  </TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>

      </div>
      <div>
                <Modal
                open={open}
                onClose={handleClose}
                aria-labelledby="modal-modal-title"
                aria-describedby="modal-modal-description"
              >
                <Box sx={style} bgcolor="black">
                  <Typography id="modal-modal-title" variant="h6" component="h2" color="white">
                    Text in a modal
                  </Typography>
                  <Typography id="modal-modal-description" color="white" sx={{ mt: 2 }}>
                    Duis mollis, est non commodo luctus, nisi erat porttitor ligula.
                  </Typography>
                </Box>
              </Modal>
                      </div>
    </div>
  );
}

export default App;
