import { useEffect, useState } from "react";
import React from "react";
import axios from "axios";
import TextField from "@mui/material/TextField";
import FormControl from "@mui/material/FormControl";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import ListItemText from "@mui/material/ListItemText";
import Select from "@mui/material/Select";
import Checkbox from "@mui/material/Checkbox";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import { useTheme } from '@mui/material/styles';
import Chip from '@mui/material/Chip';
import { Slider } from "@mui/material";
import { styled, Box } from '@mui/system';
import ModalUnstyled from '@mui/core/ModalUnstyled';
import { Dialog, Transition } from '@headlessui/react'
import { Fragment } from "react";
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';
import Database from "./Database";


export default function Content() {
  const [fetchData, setFetchData] = useState([]);
  const [mainData, setMainData] = useState([]);
  const [experience, setExperience] = useState("");
  const [skills, setSkills] = useState([]);
  const [locations, setLocations] = useState([]);
  const [skillsAtSearch, setSkillsAtSearch] = useState("");
  const [locationAtSearch, setLocationAtSearch] = useState([]);
  const [blob, setBlob] = useState([]);

  useEffect(()=> {
    axios
      .post("https://dev.weekday.technology/adhoc/candidateSearchAssignment")
      .then((response) => {
        var d = [];
        var locaData = [];
        var skillData = []
        response.data.list.map((x) =>{
          var item= x;
          var entity = item;
          entity["rawData"] = JSON.parse(item["rawData"]);
          item.rawData.map((element)=>{
            d.push(element);
          
          })
        })
        setMainData(d);
        setFetchData(d);

        d.map((ele)=>{
          locaData.push(ele.location_region)
        });
        d.map((ele)=>{
          for(var i=0; i < ele.skills.length; i++)
          skillData.push(ele.skills[i])
        });
        var skillData1 = [...new Set(skillData)]
          setSkills(skillData1);

        var locaData1 = [...new Set(locaData)]
        setLocations(locaData1);

      })
      .catch((e)=>{
        console.log("Error", e);
      });
  }, []);
 
 
  const handleFilterClick = () => { 
    let filter = [...fetchData];
    setBlob(filter)
    var i;
    if (experience) {
      console.log(experience);
      for (i = 0; i < filter.length; i++) {
        let item = filter[i];
        if (item["inferred_years_experience"] < parseInt(experience)) {
          filter.splice(i, 1);
          i--;
        }
      }
    }
  
      console.log(filter);
      if (locationAtSearch.length > 0) {
        let newData = [];
        for (i = 0; i < filter.length; i++) {
          let test = filter[i];
          for (var j = 0; j < locationAtSearch.length; j++) {
            if (test["location_region"].indexOf(locationAtSearch[j]) > -1) {
              newData.push(test);
              break;
            }
          }
        }
        filter = newData;
        setBlob(filter);
      }

          console.log(filter);
    if (skillsAtSearch) {
      console.log('filter1blob', filter)
    }
    
    console.log('filter', filter)
  setMainData(filter);
  
  };

  const handleClearFilters = () => {
    setMainData(fetchData);
    setExperience("");
    setSkillsAtSearch("");
    setLocationAtSearch([]);
  };

  const handleLocationChange = (event) => {
    const {
      target: { value },
    } = event;
    setLocationAtSearch(typeof value === "string" ? value.split(",") : value);
  };


  console.log(fetchData)
  return (
    <div class="px-2 pt-4 w-full">
      <div class="flex flex-row w-full items-center">
      <div class="w-1/4 pr-2">

      <FormControl fullWidth>
          <TextField id="standard-basic" label="Min Experience" value={skillsAtSearch} variant="standard" onChange={(e) => {
                setSkillsAtSearch(e.target.value);
              }}/>
          </FormControl>

        </div>
        <div class="w-1/4 pr-8 pl-4" >

          <FormControl fullWidth>
          <TextField id="standard-basic" label="Min Experience" value={experience} variant="standard" onChange={(e) => {
                setExperience(e.target.value);
              }}/>
          </FormControl>
          <Slider
                aria-label="Temperature"
                defaultValue={3}
                // getAriaValueText={experience}
                valueLabelDisplay="auto"
                step={1}
                marks
                min={0}
                max={10}
                value={experience}
                onChange={(e) => {
                  setExperience(e.target.value);
                }}
              />
        </div>
        <div class="w-1/4 pr-2">
          <FormControl fullWidth>
            <InputLabel id="location">Location</InputLabel>
            <Select
              sx={{ borderRadius: 0 }}
              size="small"
              labelId="location"
              id="location"
              multiple
              value={locationAtSearch}
              onChange={handleLocationChange}
              input={<OutlinedInput label="Skills" />}
              renderValue={(selected) => selected.join(", ")}
            >
              {locations?.map((obj) => (
                <MenuItem key={obj} value={obj}>
                  <Checkbox checked={locationAtSearch.indexOf(obj) > -1} />
                  <ListItemText primary={obj} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>
        <div class="w-1/4 flex flex-row">
          <div class="pr-2">
            <Button
              variant="contained"
              sx={{ borderRadius: 0 }}
              onClick={handleFilterClick}
            >
              Filter
            </Button>
          </div>
          <div>
            <Button
              variant="contained"
              sx={{ borderRadius: 0 }}
              onClick={handleClearFilters}
            >
              Clear Filters
            </Button>
          </div>
        </div>
      </div>
      <div class="pt-4">
      <Database mData={mainData}/>
      </div>
    </div>
  );
}
