import { useEffect, useState } from "react";
import React from "react";
import Table from "@mui/material/Table";
import TableBody from "@mui/material/TableBody";
import TableCell from "@mui/material/TableCell";
import TableContainer from "@mui/material/TableContainer";
import TableHead from "@mui/material/TableHead";
import TableRow from "@mui/material/TableRow";
import Paper from "@mui/material/Paper";
import axios from "axios";
import TextField from "@mui/material/TextField";
import FormControl from "@mui/material/FormControl";
import Button from "@mui/material/Button";
import MenuItem from "@mui/material/MenuItem";
import ListItemText from "@mui/material/ListItemText";
import Select from "@mui/material/Select";
import Checkbox from "@mui/material/Checkbox";
import OutlinedInput from "@mui/material/OutlinedInput";
import InputLabel from "@mui/material/InputLabel";
import skillsSelection from "./components/SkillsSearch";
import { useTheme } from '@mui/material/styles';
import Box from '@mui/material/Box';
import Chip from '@mui/material/Chip';


const ITEM_HEIGHT = 48;
const ITEM_PADDING_TOP = 8;
const MenuProps = {
  PaperProps: {
    style: {
      maxHeight: ITEM_HEIGHT * 4.5 + ITEM_PADDING_TOP,
      width: 250,
    },
  },
};

const skillsMenu = [
  "javascript",
  "jquery",
  "node.js",
  "express.js",
  "react.js",
  "mysql",
  "mongodb",
  "html",
  "mvc",
  "css",
  "matlab",
  "chemical engineering",
  "microsoft office",
  "data analysis",
  "engineering",
  "microsoft excel",
  "powerpoint",
  "research",
  "chemistry",
  "autocad",
  "microsoft word",
  "comsol",
  "thermodynamics",
  "ir spectroscopy",
  "process engineering",
  "solidworks",
  "distillation",
  "organic chemistry",
  "teamwork",
  "technical writing",
  "uv/vis",
  "nmr",
  "ftir",
  "hplc",
  "gas chromatography",
  "materials science",
  "pro/ii",
  "cascading style sheets",
  "c++",
  "python",
  "c#",
  "java",
  "check",
];

function App() {
  const [fetchData, setFetchData] = useState([]);
  const [mainData, setMainData] = useState([]);
  const [experience, setExperience] = useState("");
  const [skills, setSkills] = useState([]);

  const theme = useTheme();
  const [personName, setPersonName] = React.useState([]);


  useEffect(() => {
    axios
      .post("https://dev.weekday.technology/adhoc/candidateSearchAssignment")
      .then((response) => {
        var d = [];
        response.data.list.map((x) => {
          var item = x;
          var entity = item;
          entity["rawData"] = JSON.parse(item["rawData"]);
          item.rawData.map((element) => {
            d.push(element);
          });
        });
        setMainData(d);
        setFetchData(d);
      })
      .catch((e) => {
        console.log("Error", e);
      });
  }, []);

  const handleFilterClick = () => { 
    let filter = [...fetchData];
    var i;
    if (experience) {
      console.log(experience);
      for (i = 0; i < filter.length; i++) {
        let item = filter[i];
        if (item["inferred_years_experience"] < parseInt(experience)) {
          filter.splice(i, 1);
          i--;
        }
      }
    }
    console.log(filter);
    if (skills.length > 0) {
      let newData = [];
      for (i = 0; i < filter.length; i++) {
        let test = filter[i];
        for (var j = 0; j < skills.length; j++) {
          if (test["skills"].indexOf(skills[j]) > -1) {
            newData.push(test);
            break;
          }
        }
      }
      filter = newData;
    }
    setMainData(filter);
  };

  const handleClearFilters = () => {
    setMainData(fetchData);
    setExperience("");
    setSkills([]);
  };

  const handleSkillsChange = (event) => {
    const {
      target: { value },
    } = event;
    setSkills(typeof value === "string" ? value.split(",") : value);
  };

  return (
    <div class="px-2 pt-4 w-full">
      <div class="flex flex-row w-full items-center">
        <div class="w-1/4 pr-2">
        <FormControl fullWidth>
            <InputLabel id="skills">Skills</InputLabel>
            <Select
              sx={{ borderRadius: 0 }}
              size="small"
              labelId="skills"
              id="skills"
              multiple
              value={skills}
              onChange={handleSkillsChange}
              input={<OutlinedInput label="Skills" />}
              renderValue={(selected) => selected.join(", ")}
            >
              {skillsMenu?.map((obj) => (
                <MenuItem key={obj} value={obj}>
                  <Checkbox checked={skills.indexOf(obj) > -1} />
                  <ListItemText primary={obj} />
                </MenuItem>
              ))}
            </Select>
          </FormControl>
        </div>
          <FormControl fullWidth>
          <TextField id="standard-basic" label="Min Experience" value={experience} variant="standard" onChange={(e) => {
                setExperience(e.target.value);
              }}/>
          </FormControl>
        </div>

          <div class="w-1/4 pr-2">

        <div class="w-1/4 pr-2">
          <FormControl fullWidth>
            <TextField
              labelId="experience"
              sx={{ borderRadius: 0 }}
              id="experience"
              value={experience}
              label="Min Experience"
              size="small"
              onChange={(e) => {
                setExperience(e.target.value);
              }}
            />
          </FormControl>
        </div>
        <div class="w-1/4 flex flex-row">
          <div class="pr-2">
            <Button
              variant="contained"
              sx={{ borderRadius: 0 }}
              onClick={handleFilterClick}
            >
              Filter
            </Button>
          </div>
          <div>
            <Button
              variant="contained"
              sx={{ borderRadius: 0 }}
              onClick={handleClearFilters}
            >
              Clear Filters
            </Button>
          </div>
        </div>
      </div>
      <div class="pt-4">
        <TableContainer component={Paper}>
          <Table sx={{ minWidth: 600 }} aria-label="compare table" stickyHeader>
            <TableHead>
              <TableRow>
                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Full Name
                </TableCell>
                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Experience
                </TableCell>
                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Gender
                </TableCell>
                <TableCell
                  align="left"
                  sx={{
                    backgroundColor: "#141414",
                    color: "#fff",
                    maxHeight: "40px",
                  }}
                >
                  Location
                </TableCell>
              </TableRow>
            </TableHead>
            <TableBody>
              {mainData?.map((row, id) => (
                <TableRow key={id}>
                  <TableCell align="left">{row?.full_name}</TableCell>
                  <TableCell align="left">
                    {row?.inferred_years_experience}
                  </TableCell>
                  <TableCell align="left">{row?.gender}</TableCell>
                  <TableCell align="left">{row?.location_locality}</TableCell>
                </TableRow>
              ))}
            </TableBody>
          </Table>
        </TableContainer>
      </div>
    </div>
  );
}

export default App;
